import styled from 'styled-components'


export const HeaderContainer = styled.div `
    width: 175px;
    @media (max-width: 600px){
        width: 100px;
    }
`;

export const Logo = styled.img `
    max-width: 100%;
`;

export const HeaderWrapper = styled.header `
    max-height: 100px;
    display: flex;
    position: sticky;
    border-bottom: 2px solid var(--purple);
`;
